mapper.select:
  
		SELECT S.*,P.DISTINGUISH,P.PROJECT_NAME AS PROJECT_NAME, D.NAME AS BIANMA_NAME,
		(CASE WHEN  U.NAME IS NOT NULL THEN U.NAME WHEN  U.NAME IS NULL THEN A.REAL_NAME END ) AS CREATE_NAME  FROM project_schedule S 
		LEFT JOIN PROJECT P ON S.PROJECT_ID=P.ID LEFT JOIN SYS_DICTIONARIES  D ON S.SCHEDULE=D.BIANMA 
		LEFT JOIN sys_user U ON S.OPER_BY=U.ID  LEFT JOIN APP_USER A ON A.ID=S.OPER_BY
        WHERE  1=1 and S.ISDEL=1 and P.ISDEL=1
		
			and ID = #{pd.ID}
		
		
		    and PROJECT_NAME LIKE CONCAT(CONCAT('%', #{pd.PROJECT_NAME}),'%')
		
		
		    and PROJECT_ID LIKE CONCAT(CONCAT('%', #{pd.PROJECT_ID}),'%')
		
			
			and SCHEDULE=#{pd.SCHEDULE}
		 
			
			and DATE=#{pd.DATE}
		 
			
			and OPER_BY=#{pd.OPER_BY}

 
		SELECT count(*) counts  FROM project_schedule S 
		LEFT JOIN PROJECT P ON S.PROJECT_ID=P.ID LEFT JOIN SYS_DICTIONARIES  D ON S.SCHEDULE=D.BIANMA 
		LEFT JOIN sys_user U ON S.OPER_BY=U.ID  LEFT JOIN APP_USER A ON A.ID=S.OPER_BY
        WHERE  1=1 and S.ISDEL=1 and P.ISDEL=1
		
			and ID = #{pd.ID}
		
		
		    and PROJECT_NAME LIKE CONCAT(CONCAT('%', #{pd.PROJECT_NAME}),'%')
		
		
		    and PROJECT_ID LIKE CONCAT(CONCAT('%', #{pd.PROJECT_ID}),'%')
		
			
			and SCHEDULE=#{pd.SCHEDULE}
		 
			
			and DATE=#{pd.DATE}
		 
			
			and OPER_BY=#{pd.OPER_BY}
